mapper.select:
SELECT USERID, PASSWD, USERNAME, EMAIL, CASE WHEN UPOINT BETWEEN 8001 AND 10000 THEN 'VIP' WHEN UPOINT BETWEEN 6001 AND 8000 THEN '우수회원' WHEN UPOINT BETWEEN 4001 AND 6000 THEN '일반회원' WHEN UPOINT BETWEEN 2001 AND 4000 THEN '준회원' WHEN UPOINT BETWEEN 0 AND 2000 THEN '손님' END UPT, UPOINT, TO_CHAR( INDATE, 'YYYY-MM-DD' ) INDATE
FROM TUSER
WHERE USERID = #{ userid }

mapper.update:

UPDATE     TUSER
   SET     USERNAME = #{ username }
         , USERID   = #{ userid }
         , PASSWD   = #{ passwd }
         , EMAIL    = #{ email }
         , UPOINT   = #{ upoint }
 WHERE     USERID = #{ userid }
